import { useState, useEffect } from "react"
import AddChannel from "./AddChannel"
import Messages from "./Messages"
import TextBox from "./TextBox"

export default () => {

    let channelArray:string[] = []
    const [channels, setChannels] = useState([])
    useEffect(() =>{
        console.log(channels)
    }, [channels])
    const handleNewChannel = (newChannel:any) =>{
        if (channels.includes(newChannel.target.value)) {}
        else {
            setChannels((prevValue) => [...prevValue, newChannel.target.value])
        }    
    }

    return (
        <div className="ChatBox">
            <Messages channels={channels}/>
            <TextBox onChange={(e : any) => alert(e)}/>
            <AddChannel onSubmit={(e: any) => handleNewChannel(e)}/>
        </div>
    )
}
