import { useState } from "react"
export default (props) => {
    const [text, setText] = useState("")
    let message = ""
    return (
        <>
        <form>
        <input
            type = "text"
            value = {text}
            onChange={e => {
                let message = (e.target.value)
                setText(message)
                console.log(text)
            }} />
        </form>
        <h1>Hi!{message}</h1>
        </>
    )
}
