import { useState } from "react";
const tmi = require("tmi.js");
let id = 0

export default (props : any) => {
    const client = new tmi.Client({
        channels: ["pigeonapology"]
    });
    const [messages, setMessages] = useState([])
    let tempArray : any = [];

    client.connect();
    client.on('message', (channel : string, tags: any, message: string, self:any) => {
        // "Alca: Hello, World!"
        let tempID = id;
        id += 1;
        tempArray.push(
            {value: <><b style={{color: "red"}}>{channel}: </b><b style={{color: "green"}}>{tags}: </b> <p>{message}</p></>,
            key: tempID}
        )
        setMessages(tempArray)
        console.log(messages)
    });
    return (
        <>
        <ul>
            {messages.map((value, key) =>  <li key={key}>{value}</li>)}
        </ul>
        </>
    )
}
